INSERT INTO `aboutus` VALUES (1,'Giới thiệu website du lịch\'','<div class=\"content\">\r\n<h2><span style=\"color:#008000\">TẦM NH&Igrave;N&#39;&#39;</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#800000\">Tr&ecirc;n cơ sở ph&aacute;t triển bền vững sau gần 20 năm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển, Vietravel hướng đến trở th&agrave;nh 1 trong 10 c&ocirc;ng ty lữ h&agrave;nh h&agrave;ng đầu khu vực Đ&ocirc;ng Nam &Aacute; v&agrave;o năm 2015. Đến năm 2020. Vietravel phấn đấu trở th&agrave;nh 1 trong 10 c&ocirc;ng ty du lịch h&agrave;ng đầu ch&acirc;u &Aacute; v&agrave; trở th&agrave;nh Top Tập đo&agrave;n lữ h&agrave;nh h&agrave;ng đầu khu vực Ch&acirc;u &Aacute;.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#a52a2a\"><span style=\"color:#800000\">Đ&acirc;y l&agrave; tầm nh&igrave;n chiến lược v&agrave; đầy thử th&aacute;ch nhưng với một mục ti&ecirc;u chung, Vietravel đ&atilde; v&agrave; đang hiện thực ho&aacute; những mục ti&ecirc;u chiến lược của m&igrave;nh</span>.</span></p>\r\n\r\n<h2><span style=\"color:rgb(0, 128, 0)\">SỨ MỆNH&nbsp;</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#800000\"><span style=\"font-size:12px\"><strong>Mang lại cảm x&uacute;c thăng hoa cho du kh&aacute;ch trong mỗi h&agrave;nh tr&igrave;nh</strong></span>&nbsp;-&nbsp;<span style=\"font-size:12px\">Đ&acirc;y l&agrave; mục ti&ecirc;u v&agrave; l&agrave; sứ mệnh Vietravel cam kết v&agrave; nỗ lực mang lại cho du kh&aacute;ch. Vietravel trở th&agrave;nh người bạn đồng h&agrave;nh c&ugrave;ng du kh&aacute;ch trong mọi h&agrave;nh tr&igrave;nh du lịch v&agrave; tạo ra những gi&aacute; trị tốt đẹp. Tại Vietravel, du lịch kh&ocirc;ng những l&agrave; h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; m&agrave; c&ograve;n l&agrave; h&agrave;nh tr&igrave;nh sẻ chia, thể hiện dấu ấn kh&aacute;c biệt của Thương hiệu Vietravel từ 3 thuộc t&iacute;nh thương hiệu: Sự chuy&ecirc;n nghiệp, mang lại cảm x&uacute;c thăng hoa cho kh&aacute;ch h&agrave;ng v&agrave; những gi&aacute; trị gia tăng hấp dẫn cho du kh&aacute;ch sau mỗi chuyến đi.&nbsp;</span></span></p>\r\n\r\n<h2><span style=\"color:#008000\">TRIẾT L&Yacute; KINH DOANH&nbsp;</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#800000\"><strong>Kh&aacute;ch h&agrave;ng l&agrave; trung t&acirc;m</strong>: Vietravel lu&ocirc;n khẳng định kh&aacute;ch h&agrave;ng l&agrave; trung t&acirc;m của mọi hoạt động kinh doanh m&agrave; Vietravel hướng đến, v&igrave; kh&aacute;ch h&agrave;ng l&agrave; người g&oacute;p phần to lớn x&acirc;y dựng n&ecirc;n thương hiệu Vietravel.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#800000\"><strong>Chuy&ecirc;n nghiệp l&agrave; thước đo</strong>: Khẳng định uy t&iacute;n thương hiệu, với mục ti&ecirc;u kh&ocirc;ng ngừng ph&aacute;t triển ho&agrave;n thiện để đạt đến những tầm cao mới trong định hướng chiến lược vươn ra thế giới. &nbsp; &nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#800000\"><strong>Chất lượng l&agrave; danh dự</strong>: Vietravel cam kết chất lượng dịch vụ cung cấp đến kh&aacute;ch h&agrave;ng l&agrave; tốt nhất, điều đ&oacute; tạo n&ecirc;n sự kh&aacute;c biệt so với thị trường, Vietravel xem đ&oacute; l&agrave; tr&aacute;ch nhiệm, l&agrave; danh dự của mỗi c&aacute;n bộ - nh&acirc;n vi&ecirc;n.&nbsp;</span><img alt=\"laugh\" src=\"http://tour.pro:8080/library/ckeditor/plugins/smiley/images/teeth_smile.png\" style=\"height:23px; width:23px\" title=\"laugh\" /></p>\r\n</div>\r\n');
/*!40000 ALTER TABLE `aboutus` ENABLE KEYS */;
UNLOCK TABLES;

